INSERT INTO `ecs_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('3839', '14', 'TYT003839', '【南京】爱康国宾体检卡 D套餐 (男) 2800元面值', '+', '10', '251', '', '0', '0.000', '3360.00', '2800.00', '0.00', '0', '0', '0', '', '', '<table border=\"0\" cellspacing=\"1\" cellpadding=\"5\" width=\"750\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#eeeeee\" colspan=\"2\" style=\"font-size: 14px; div: \"><strong>商品说明</strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" colspan=\"2\"><span class=\"STYLE3\"><strong>基本信息</strong></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" width=\"362\"><span style=\"font-size: 12px\">商品名称</span>：【南京】爱康国宾体检卡 D套餐 (男)</td>\r\n            <td bgcolor=\"#f3f3f3\" width=\"365\"><span style=\"font-size: 12px\">品牌：<font style=\"background-color: #f3f3f3\">爱康</font></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" width=\"362\">面值：2800元</td>\r\n            <td bgcolor=\"#f3f3f3\" width=\"365\">&nbsp;</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" colspan=\"2\"><strong><span class=\"STYLE3\">规格参数</span></strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\"><span class=\"STYLE3\">使用截止日期： 2012年6月30日</span></td>\r\n            <td bgcolor=\"#f3f3f3\"><span class=\"STYLE3\">折扣券形式：实体券</span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\"><span class=\"STYLE3\">优惠券适用省份：江苏</span></td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\"><span class=\"STYLE3\">&nbsp;优惠券适用城市：南京</span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p>D<strong><font size=\"4\">套餐 (男士)体验项目：见下表。</font></strong></p>\r\n<p>&nbsp;&nbsp;&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>\r\n<table border=\"1\" cellspacing=\"0\" bordercolor=\"#c0c0c0\" cellpadding=\"5\" width=\"96%\" align=\"center\" style=\"border-collapse: collapse\">\r\n    <tbody>\r\n        <tr align=\"center\" bgcolor=\"#efefef\">\r\n            <td bgcolor=\"#efefef\" width=\"60\" align=\"center\">序号</td>\r\n            <td bgcolor=\"#efefef\" width=\"117\" align=\"center\">体检项目</td>\r\n            <td bgcolor=\"#efefef\" align=\"center\">健检目的</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">01</td>\r\n            <td align=\"center\">一般检查A</td>\r\n            <td align=\"left\">通过仪器测量人体身高、体重及血压，科学判断体重是否标准、血压是否正常。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">02</td>\r\n            <td align=\"center\">人体成分分析（成人）</td>\r\n            <td align=\"left\">&nbsp;</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">03</td>\r\n            <td align=\"center\">内科</td>\r\n            <td align=\"left\">通过视、触、叩、听检查心、肺、肝、脾等重要脏器的基本状况，发现常见疾病的相关征兆，或初步排除常见疾病。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">04</td>\r\n            <td align=\"center\">男外科</td>\r\n            <td align=\"left\">通过体格检查，检查男性皮肤、甲状腺、脊柱四肢、前列腺、外生殖器等重要脏器基本情况，发现常见外科疾病的相关征兆，或初步排除外科常见疾病。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">05</td>\r\n            <td align=\"center\">视力 色觉</td>\r\n            <td align=\"left\">了解视力状况，判断眼睛视力、色觉功能。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">06</td>\r\n            <td align=\"center\">外眼</td>\r\n            <td align=\"left\">检查眼睑、泪囊、结膜、眼球是否存在异常情况。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">07</td>\r\n            <td align=\"center\">裂隙灯检查</td>\r\n            <td align=\"left\">通过裂隙灯检查巩膜、虹膜、角膜、瞳孔、玻璃体等有无异常情况。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">08</td>\r\n            <td align=\"center\">眼底镜检查</td>\r\n            <td align=\"left\">通过眼底镜检查眼底视网膜、视神经乳头和视网膜中央血管等有无异常情况。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">09</td>\r\n            <td align=\"center\">非接触性眼压测量</td>\r\n            <td align=\"left\">用于原发性青光眼的早期诊断、病情评估、疗法选择、疗效评价。以可控空气脉冲力测量眼压，可避免眼压计接触角膜所致的交叉感染与对角膜表面麻醉剂的过敏可能性。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">10</td>\r\n            <td align=\"center\">耳鼻咽喉科</td>\r\n            <td align=\"left\">通过对耳、鼻、咽、扁桃喉等器官的常规检查，初步筛查常见疾病。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">11</td>\r\n            <td align=\"center\">鼻咽镜检查</td>\r\n            <td align=\"left\">通过鼻咽镜检查，了解鼻咽部有无异常情况。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">12</td>\r\n            <td align=\"center\">间接喉镜检查</td>\r\n            <td align=\"left\">通过间接喉镜检查，了解喉部有无异常情况存在。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">13</td>\r\n            <td align=\"center\">口腔科</td>\r\n            <td align=\"left\">口腔常规检查，全面了解口腔健康状况，及时发现口腔科常见疾病。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">14</td>\r\n            <td align=\"center\">经络检测</td>\r\n            <td align=\"left\">通过检测出的生物电值的异常变化，提示人体疾病状况并初步判断倾向性疾病和潜在性疾病，有利于疾病的早期预防，早期诊断和早期治疗。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">15</td>\r\n            <td align=\"center\">血常规</td>\r\n            <td align=\"left\">通过检测血液细胞的计数及不同种类细胞、成分的分类来反映身体状况，如：贫血、感染等等。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">16</td>\r\n            <td align=\"center\">尿常规</td>\r\n            <td align=\"left\">用于检查泌尿系统疾病，如泌尿系统感染、肿瘤、结石及了解肾功能，还可用于协助检查其他系统疾病，如糖尿病、高血压、肝炎等。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">17</td>\r\n            <td align=\"center\">便常规</td>\r\n            <td align=\"left\">对了解胃肠道及肝、胆、胰腺等器官病变及判断胃肠、胰腺、肝胆系统功能有重要价值。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">18</td>\r\n            <td align=\"center\">便隐血</td>\r\n            <td align=\"left\">检查粪便中隐藏的红细胞或血红蛋白的一项实验。这对检出引起消化道出血的疾病：如急慢性胃炎、胃十二指肠溃疡、胃癌、肠癌等是非常有用的。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">19</td>\r\n            <td align=\"center\">肝功全套（11项）</td>\r\n            <td align=\"left\">初步了解肝脏功能状况，是否有肝功能减退、异常等。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">20</td>\r\n            <td align=\"center\">肾功三项</td>\r\n            <td align=\"left\">用于评估肾功能。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">21</td>\r\n            <td align=\"center\">空腹血糖(FBG)</td>\r\n            <td align=\"left\">评价人体空腹状态下糖代谢是否正常，评估糖尿病患者空腹血糖控制是否达标。空腹血糖是诊断糖代谢紊乱的最常用和最重要指标。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">22</td>\r\n            <td align=\"center\">糖化血红蛋白(HbA1C)</td>\r\n            <td align=\"left\">检测HbA1c对高血糖、尤在血糖和尿糖波动较大时有特殊诊断意义；反映近2-3个月的平均血糖水平；用于筛检糖尿病、预测血管并发症、鉴别高血糖原因，评价糖尿病控制程度。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">23</td>\r\n            <td align=\"center\">血脂四项</td>\r\n            <td align=\"left\">测定血清中血脂含量，它们的增高或降低与动脉粥样硬化的形成有很大的关系。用于评价受检者的脂肪代谢水平，血脂代谢紊乱评价、动脉粥样硬化性疾病危险性预测和营养学评价。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">24</td>\r\n            <td align=\"center\">血流变</td>\r\n            <td align=\"left\">该检查对某些疾病的诊断、预防、观察疗效等都有重要意义。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">25</td>\r\n            <td align=\"center\">幽门螺旋杆菌抗体(Hp-Ab)</td>\r\n            <td align=\"left\">幽门螺杆菌抗体阳性提示既往感染过或正在感染幽门螺杆菌，它与胃十二指肠溃疡、胃部炎症、胃癌的发生密切关联。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">26</td>\r\n            <td align=\"center\">甲胎蛋白定量(AFP)</td>\r\n            <td align=\"left\">对原发性肝癌的诊断、疗效观察和预后评估有重要的临床意义。在卵巢、胃、胰腺癌、睾丸癌等肿瘤及肝炎、肝硬化等疾病也有异常发现。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">27</td>\r\n            <td align=\"center\">癌胚抗原定量(CEA)</td>\r\n            <td align=\"left\">系广谱性肿瘤标志物，对大肠癌、胰腺癌的筛查、疗效观察和预后评估有重要的临床意义。在胃、乳腺、肺癌等也可升高。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">28</td>\r\n            <td align=\"center\">神经元特异性烯醇化酶(NSE)</td>\r\n            <td align=\"left\">对小细胞肺癌、神经母细胞瘤的早期诊断及评估预后有临床重要意义。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">29</td>\r\n            <td align=\"center\">细胞角蛋白(Cyfra21-1)</td>\r\n            <td align=\"left\">对肺癌的早期诊断及评估预后，乳腺、卵巢、食道、胃肠道癌的筛查有临床重要意义。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">30</td>\r\n            <td align=\"center\">微量元素六项</td>\r\n            <td align=\"left\">通过对人体微量和常量元素的检测，了解机体微量营养素的代谢情况，对了解机体组构成分、生理功能正常与否有重要意义。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">31</td>\r\n            <td align=\"center\">腹部彩超</td>\r\n            <td align=\"left\">对人体腹部内脏器官（肝、胆、脾、胰、双肾）的状况和各种病变（如肿瘤、结石、积水、脂肪肝等）提供高清晰度的彩色动态超声断层图像判断，依病灶周围血管情况、病灶内血流血供情况-良恶性病变鉴别；判断肾动脉狭窄等。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">32</td>\r\n            <td align=\"center\">前列腺彩超</td>\r\n            <td align=\"left\">通过彩色超声仪器检查更清晰地观察前列腺大小、形态、结构等情况，判断有无前列腺增大、囊肿、结石，恶性病变等。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">33</td>\r\n            <td align=\"center\">甲状腺彩超</td>\r\n            <td align=\"left\">通过彩色超声仪器更清晰地观察甲状腺肿物、结节、肿大、炎症；可发现甲状腺肿、甲状腺囊肿、甲状腺炎、甲状腺瘤、甲状腺癌等疾病。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">34</td>\r\n            <td align=\"center\">颈动脉彩超</td>\r\n            <td align=\"left\">通过彩色超声检测颈动脉结构和动脉粥样硬化斑形态、范围、性质、动脉狭窄程度等；早期发现动脉血管病变，为有效预防和减少冠心病、缺血性脑血管病等心脑血管疾病发病提供客观的血流动力学依据。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">35</td>\r\n            <td align=\"center\">胸部正位</td>\r\n            <td align=\"left\">通过X线拍片检查两肺、心脏、纵隔、膈、胸膜，判断有无炎症、肿瘤等。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">36</td>\r\n            <td align=\"center\">颈椎侧位</td>\r\n            <td align=\"left\">通过X线拍片检查，查看椎体有无先天性畸形、增生、退行性变、椎间孔狭窄、肿物等情况。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">37</td>\r\n            <td align=\"center\">心电图（12导）</td>\r\n            <td align=\"left\">通过在体表特定部位同步记录和分析心脏每一个心动周期所产生电活动变化的曲线图形，为心脏疾病诊断、疗效评价、预后评估提供重要的依据。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">38</td>\r\n            <td align=\"center\">骨密度检查</td>\r\n            <td align=\"left\">通过骨密度仪检查骨质密度，早期发现骨量减少及估计骨质疏松的程度，及时进行有效防治。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">39</td>\r\n            <td align=\"center\">动脉硬化检测</td>\r\n            <td align=\"left\">对人体动脉血管做总体性评估，便于早发现动脉硬化。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">40</td>\r\n            <td align=\"center\">早餐</td>\r\n            <td align=\"left\">&nbsp;</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">41</td>\r\n            <td align=\"center\">个检报告</td>\r\n            <td align=\"left\">根据个人健康状况及疾病发展趋势，由专家分析主要健康问题，确定相关危险因素，并提供一份完整的个性化的体检报告解读及基本健康改善指导原则。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">42</td>\r\n            <td align=\"center\">人工健康与疾病评估（男14项）</td>\r\n            <td align=\"left\">预测个人未来5年内患冠心病、中风、糖尿病、肿瘤等种慢性疾病的危险性。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">43</td>\r\n            <td align=\"center\">个检报告及高级保健专家咨询</td>\r\n            <td align=\"left\">根据个人健康状况及疾病发展趋势，由专家面询分析主要健康问题，确定相关危险因素，并提供一份完整的个性化的体检报告解读及基本健康改善指导原则。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">44</td>\r\n            <td align=\"center\">采血材料费</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', 'images/201201/thumb_img/3839_thumb_G_1326394013190.jpg', 'images/201201/goods_img/3839_G_1326394013363.jpg', 'images/201201/source_img/3839_G_1326394013211.jpg', '1', '', '1', '1', '0', '2800', '1326307715', '100', '0', '0', '0', '0', '0', '0', '1326394013', '0', '', '2450', '2800', '0', '');
INSERT INTO `ecs_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('3840', '14', 'TYT003840', '【南京】爱康国宾体检卡 B套餐 (女) 820元面值', '+', '6', '251', '', '0', '0.000', '984.00', '820.00', '0.00', '0', '0', '0', '', '', '<table border=\"0\" cellspacing=\"1\" cellpadding=\"5\" width=\"750\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#eeeeee\" colspan=\"2\" style=\"font-size: 14px; div: \"><strong>商品说明</strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" colspan=\"2\"><span class=\"STYLE3\"><strong>基本信息</strong></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" width=\"362\"><span style=\"font-size: 12px\">商品名称</span>：【南京】爱康国宾体检卡 B套餐 （女）</td>\r\n            <td bgcolor=\"#f3f3f3\" width=\"365\"><span style=\"font-size: 12px\">品牌：<font style=\"background-color: #f3f3f3\">爱康</font></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" width=\"362\">面值：820元</td>\r\n            <td bgcolor=\"#f3f3f3\" width=\"365\">&nbsp;</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" colspan=\"2\"><strong><span class=\"STYLE3\">规格参数</span></strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\"><span class=\"STYLE3\">使用截止日期： 2012年6月30日</span></td>\r\n            <td bgcolor=\"#f3f3f3\"><span class=\"STYLE3\">折扣券形式：实体券</span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\"><span class=\"STYLE3\">优惠券适用省份：江苏</span></td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\"><span class=\"STYLE3\">&nbsp;优惠券适用城市：南京</span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p><strong><font size=\"4\">B套餐 (女士)体验项目：见下表。</font></strong></p>\r\n<p>&nbsp;&nbsp;</p>\r\n<p>\r\n<table border=\"1\" cellspacing=\"0\" bordercolor=\"#c0c0c0\" cellpadding=\"5\" width=\"96%\" align=\"center\" style=\"border-collapse: collapse\">\r\n    <tbody>\r\n        <tr align=\"center\" bgcolor=\"#efefef\">\r\n            <td bgcolor=\"#efefef\" width=\"60\" align=\"center\">序号</td>\r\n            <td bgcolor=\"#efefef\" width=\"117\" align=\"center\">体检项目</td>\r\n            <td bgcolor=\"#efefef\" align=\"center\">健检目的</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">01</td>\r\n            <td align=\"center\">一般检查A</td>\r\n            <td align=\"left\">通过仪器测量人体身高、体重及血压，科学判断体重是否标准、血压是否正常。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">02</td>\r\n            <td align=\"center\">内科</td>\r\n            <td align=\"left\">通过视、触、叩、听检查心、肺、肝、脾等重要脏器的基本状况，发现常见疾病的相关征兆，或初步排除常见疾病。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">03</td>\r\n            <td align=\"center\">女外科</td>\r\n            <td align=\"left\">通过体格检查，检查女性皮肤、甲状腺、脊柱四肢、乳腺等重要脏器基本情况，发现常见外科疾病的相关征兆，或初步排除外科常见疾病。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">04</td>\r\n            <td align=\"center\">妇科检查</td>\r\n            <td align=\"left\">通过妇科触诊及仪器检查方法，发现常见妇科疾病的相关征兆，或初步排除妇科常见疾病。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">05</td>\r\n            <td align=\"center\">白带常规</td>\r\n            <td align=\"left\">用于检查阴道内有无滴虫、念珠菌，同时还可确定阴道清洁度，是筛查阴道炎的有效手段。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">06</td>\r\n            <td align=\"center\">宫颈刮片</td>\r\n            <td align=\"left\">即子宫颈脱落细胞的巴氏染色检查。是简便易行的早期发现宫颈癌的重要手段。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">07</td>\r\n            <td align=\"center\">视力 色觉</td>\r\n            <td align=\"left\">了解视力状况，判断眼睛视力、色觉功能。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">08</td>\r\n            <td align=\"center\">外眼</td>\r\n            <td align=\"left\">检查眼睑、泪囊、结膜、眼球是否存在异常情况。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">09</td>\r\n            <td align=\"center\">裂隙灯检查</td>\r\n            <td align=\"left\">通过裂隙灯检查巩膜、虹膜、角膜、瞳孔、玻璃体等有无异常情况。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">10</td>\r\n            <td align=\"center\">耳鼻咽喉科</td>\r\n            <td align=\"left\">通过对耳、鼻、咽、扁桃喉等器官的常规检查，初步筛查常见疾病。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">11</td>\r\n            <td align=\"center\">口腔科</td>\r\n            <td align=\"left\">口腔常规检查，全面了解口腔健康状况，及时发现口腔科常见疾病。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">12</td>\r\n            <td align=\"center\">血常规</td>\r\n            <td align=\"left\">通过检测血液细胞的计数及不同种类细胞、成分的分类来反映身体状况，如：贫血、感染等等。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">13</td>\r\n            <td align=\"center\">尿常规</td>\r\n            <td align=\"left\">用于检查泌尿系统疾病，如泌尿系统感染、肿瘤、结石及了解肾功能，还可用于协助检查其他系统疾病，如糖尿病、高血压、肝炎等。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">14</td>\r\n            <td align=\"center\">便常规</td>\r\n            <td align=\"left\">对了解胃肠道及肝、胆、胰腺等器官病变及判断胃肠、胰腺、肝胆系统功能有重要价值。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">15</td>\r\n            <td align=\"center\">便隐血</td>\r\n            <td align=\"left\">检查粪便中隐藏的红细胞或血红蛋白的一项实验。这对检出引起消化道出血的疾病：如急慢性胃炎、胃十二指肠溃疡、胃癌、肠癌等是非常有用的。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">16</td>\r\n            <td align=\"center\">肝功三项</td>\r\n            <td align=\"left\">更好地了解肝胆系统功能状况。 ALT、 AST、GGT主要存在于肝胆心脑肾组织细胞内，肝细胞损伤越大ALT、 AST、GGT就越高。急慢性肝炎、脂肪肝、肝硬化、肝癌、胆管炎等疾病均可引起ALT、 AST、GGT升高。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">17</td>\r\n            <td align=\"center\">尿素氮(BUN)</td>\r\n            <td align=\"left\">用于肾功能评价，测定肾功能损害程度及估计预后；血尿酸增高对高尿酸血症、痛风有诊断意义。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">18</td>\r\n            <td align=\"center\">肌酐(Cr)</td>\r\n            <td align=\"left\">用于肾功能评价，测定肾功能损害程度及估计预后；血尿酸增高对高尿酸血症、痛风有诊断意义。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">19</td>\r\n            <td align=\"center\">尿酸(UA)</td>\r\n            <td align=\"left\">用于肾功能评价，测定肾功能损害程度及估计预后；血尿酸增高对高尿酸血症、痛风有诊断意义。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">20</td>\r\n            <td align=\"center\">空腹血糖(FBG)</td>\r\n            <td align=\"left\">评价人体空腹状态下糖代谢是否正常，评估糖尿病患者空腹血糖控制是否达标。空腹血糖是诊断糖代谢紊乱的最常用和最重要指标。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">21</td>\r\n            <td align=\"center\">血脂四项</td>\r\n            <td align=\"left\">测定血清中血脂含量，它们的增高或降低与动脉粥样硬化的形成有很大的关系。用于评价受检者的脂肪代谢水平，血脂代谢紊乱评价、动脉粥样硬化性疾病危险性预测和营养学评价。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">22</td>\r\n            <td align=\"center\">幽门螺旋杆菌抗体(Hp-Ab)</td>\r\n            <td align=\"left\">幽门螺杆菌抗体阳性提示既往感染过或正在感染幽门螺杆菌，它与胃十二指肠溃疡、胃部炎症、胃癌的发生密切关联。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">23</td>\r\n            <td align=\"center\">甲胎蛋白定量(AFP)</td>\r\n            <td align=\"left\">对原发性肝癌的诊断、疗效观察和预后评估有重要的临床意义。在卵巢、胃、胰腺癌、睾丸癌等肿瘤及肝炎、肝硬化等疾病也有异常发现。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">24</td>\r\n            <td align=\"center\">癌胚抗原定量(CEA)</td>\r\n            <td align=\"left\">系广谱性肿瘤标志物，对大肠癌、胰腺癌的筛查、疗效观察和预后评估有重要的临床意义。在胃、乳腺、肺癌等也可升高。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">25</td>\r\n            <td align=\"center\">腹部彩超</td>\r\n            <td align=\"left\">对人体腹部内脏器官（肝、胆、脾、胰、双肾）的状况和各种病变（如肿瘤、结石、积水、脂肪肝等）提供高清晰度的彩色动态超声断层图像判断，依病灶周围血管情况、病灶内血流血供情况-良恶性病变鉴别；判断肾动脉狭窄等。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">26</td>\r\n            <td align=\"center\">子宫、附件彩超</td>\r\n            <td align=\"left\">通过彩色超声仪器清晰地观察子宫及附件（卵巢、输卵管）大小、形态结构及内部回声的情况，鉴别正常和异常，了解病变的性质，判别有无恶性病变。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">27</td>\r\n            <td align=\"center\">胸部正位</td>\r\n            <td align=\"left\">通过X线拍片检查两肺、心脏、纵隔、膈、胸膜，判断有无炎症、肿瘤等。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">28</td>\r\n            <td align=\"center\">心电图</td>\r\n            <td align=\"left\">通过在体表特定部位同步记录和分析心脏每一个心动周期所产生电活动变化的曲线图形，为心脏疾病诊断、疗效评价、预后评估提供重要的依据。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">29</td>\r\n            <td align=\"center\">早餐</td>\r\n            <td align=\"left\">&nbsp;</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">30</td>\r\n            <td align=\"center\">普通早餐</td>\r\n            <td align=\"left\">&nbsp;</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">31</td>\r\n            <td align=\"center\">个检报告</td>\r\n            <td align=\"left\">根据个人健康状况及疾病发展趋势，由专家分析主要健康问题，确定相关危险因素，并提供一份完整的个性化的体检报告解读及基本健康改善指导原则。</td>\r\n        </tr>\r\n        <tr bgcolor=\"#ffffff\">\r\n            <td align=\"center\">32</td>\r\n            <td align=\"center\">采血材料费</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>', 'images/201201/thumb_img/3840_thumb_G_1326393968828.jpg', 'images/201201/goods_img/3840_G_1326393968207.jpg', 'images/201201/source_img/3840_G_1326393968783.jpg', '1', '', '1', '1', '0', '820', '1326307829', '100', '0', '0', '0', '0', '0', '0', '1326393968', '0', '', '718', '820', '0', '');
