INSERT INTO `ecs_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check` ) VALUES  ('3134', '85', 'TYT003134', '美的 加湿器 S30U-G', '+', '13', '2', '', '0', '0.000', '430.80', '359.00', '0.00', '0', '0', '0', '', '', '<table align=\"center\" cellspacing=\"1\" cellpadding=\"5\" border=\"0\" width=\"750\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#eeeeee\" colspan=\"2\" style=\"font-size: 14px;&gt;\r\n            &lt;div align=\"><strong>商品说明</strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" colspan=\"2\"><span class=\"STYLE3\"><strong>基本信息</strong></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" width=\"362\"><span style=\"font-size: 12px\">商品名称</span>：<font id=\"ECS_MEMBERZONE\">美的加湿器 S30U-G</font></td>\r\n            <td bgcolor=\"#f3f3f3\" width=\"365\"><span style=\"font-size: 12px\">品牌</span>：<span style=\"font-size: 12px\">美的（Midea）</span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\"><span style=\"font-size: 12px\">型号</span>：<span class=\"STYLE3\">S30U-G甘泉</span></td>\r\n            <td bgcolor=\"#f3f3f3\"><span class=\"STYLE3\">外观颜色：蓝色</span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">类别：超声波</td>\r\n            <td bgcolor=\"#f3f3f3\">外观式样：台式</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\"><span class=\"STYLE3\">产地：中山</span></td>\r\n            <td bgcolor=\"#f3f3f3\">售后服务：全国联保</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" colspan=\"2\"><strong><span class=\"STYLE3\">规格参数</span></strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\"><span class=\"STYLE3\">额定加湿量：250ml/h-280ml/h</span></td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\"><span class=\"STYLE3\">噪声：36db以下</span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">额定功率：25W</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\"><span class=\"STYLE3\">额定电压：220V</span></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">适用面积：21㎡-30㎡</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\">整机尺寸：220*220*280mm</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">净重：1.2Kg</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\">容量：3L</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">毛重：1.7Kg</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\">操作方式：旋钮式</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">加湿方式：出雾</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\">是否使用滤芯：否</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">更换滤芯提示：不支持</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\">加湿模式：标准、节能</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">缺水断电保护：支持</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\">注水口位置：水箱底部</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">定时功能：不支持</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\">&nbsp;散热片材质：硅胶散热</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">出雾口：活性碳型</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\">出雾口数量：一个</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">LED显示屏：不支持</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\">负离子功能：不支持</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\">银离子过滤网：不支持</td>\r\n            <td bgcolor=\"#f3f3f3\" valign=\"middle\">&nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<table align=\"center\" cellspacing=\"1\" cellpadding=\"5\" border=\"0\" width=\"750\" style=\"margin-top:15px;\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#eeeeee\" class=\"STYLE4\"><strong>包装清单</strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor=\"#f3f3f3\" width=\"750\"><span class=\"STYLE3\">加湿器 &times; 1 说明书 &times; 1 保修卡 &times; 1 水箱刷 &times; 1 除垢剂 &times; 1</span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<table align=\"center\" cellspacing=\"1\" cellpadding=\"5\" border=\"0\" width=\"750\" style=\"margin-top:15px;\">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor=\"#eeeeee\" style=\"font-size: 14px;\"><strong>商品信息</strong></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p><strong><span style=\"font-size: larger;\">美的经典趣系列空气加湿器 S30U-G甘泉 建议使用面积约为25平方米</span></strong><br />\r\n1.离心风机：采用&ldquo;离心风轮&rdquo;，散热快，使用寿命更长且降低噪音，低于国标要求的38分贝 。<br />\r\n2.最优化风道设计：喷出超细雾气，加湿效率更高，有效避免桌面积水。<br />\r\n3.进风口活性炭过滤网：位于加湿器的进风口处，有效过滤空气中的浮尘杂质，消除加湿异味，净化空气并保护机内原件。<br />\r\n4.缺水、过载自动保护功能：当加湿器缺水（指示灯闪烁）、水箱缺失或电压不稳时，美的加湿器均能自动关闭电源开关，防止发生危险。<br />\r\n5.水箱提起保护：普通加湿器在正常工作状态下，当直接提起水箱时，震荡器还在继续工作。这样会使底座中的水珠溅散到加湿器周围台面，而且，若此时用户（尤其是小孩）将手指靠近或伸入高频工作着的震荡器中，则可能被超声波伤害。<br />\r\n<img height=\"218\" width=\"750\" src=\"/images/upload/Image/Zg8CGFa3PJPWtEVs.jpg\" alt=\"\" /><img height=\"227\" width=\"750\" src=\"/images/upload/Image/OjfoBJxjEbEVwT48.jpg\" alt=\"\" /></p>\r\n<p><img src=\"/images/upload/Image/5K3bwvao2cOvK1Bp.jpg\" alt=\"\" /><img height=\"350\" width=\"350\" src=\"/images/upload/Image/UKJnkYb9piHES11k.jpg\" alt=\"\" /><img height=\"350\" width=\"350\" src=\"/images/upload/Image/jjc2OGSDu2Uracwo.jpg\" alt=\"\" /></p>\r\n<table cellspacing=\"1\" cellpadding=\"5\" border=\"0\" width=\"750\">\r\n    <tbody>\r\n        <tr>\r\n            <td><span style=\"font-size: larger;\"><strong>采用6重全效净化滤芯</strong></span><br />\r\n            采用6重全效净化滤芯，杀菌消毒除白粉，舒适更健康：<br />\r\n            第一重：无纺布过滤；第二重：美的美诺纳米抗菌因子层；第三重：水用活性炭层；第四重：陶瓷球吸附层；第五重：麦饭石过滤层；第六层：负离子陶瓷球过滤层。</td>\r\n            <td><strong><span style=\"font-size: larger;\">双重安全措施</span></strong><br />\r\n            美的加湿器率先采用儿童安全保护功能，在加湿过程中若提起水箱，电控系统及时断电，震荡片停止工作，防止老人小孩不小心受伤，同时防止水珠渐到桌子上，其次如缺水会自动断电，放心又安全。</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<table cellspacing=\"1\" cellpadding=\"5\" border=\"0\" width=\"750\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img height=\"350\" width=\"350\" src=\"/images/upload/Image/ywUGzv5CMCZeefip.jpg\" alt=\"\" /></td>\r\n            <td><img height=\"350\" width=\"350\" src=\"/images/upload/Image/11BmxceWv65bmzLh.jpg\" alt=\"\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<table cellspacing=\"1\" cellpadding=\"5\" border=\"0\" width=\"750\">\r\n    <tbody>\r\n        <tr>\r\n            <td><strong><span style=\"font-size: larger;\">人性化设计</span></strong><br />\r\n            防滑橡胶垫，不易歪斜，建议使用时请平稳放置，以免水箱中的水溢出。</td>\r\n            <td><strong><span style=\"font-size: larger;\">建议使用面积约为25平方米</span></strong><br />\r\n            此款机器建议使用地点为卧室、书房、客厅等地，请平稳防止，以免造成水箱中的水溢出。</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>', 'images/201201/thumb_img/3134_thumb_G_1326067320200.jpg', 'images/201201/goods_img/3134_G_1326067320763.jpg', 'images/201201/source_img/3134_G_1326067319166.jpg', '1', '', '1', '1', '0', '359', '1324928170', '100', '0', '0', '0', '0', '0', '0', '1326221546', '0', '', '356', '359', '0', '');
